import React, { useState, useEffect } from 'react';
import axios from 'axios'

const App = () => {
  const [countries, setCountries] = useState([])
  const [query, setQuery] = useState('')
  const [show, setShow] = useState({ view: false, country: {} })


  useEffect(() => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data)
      })
  }, [])
  console.log(countries)

  const filteredCountries = query.trim()
    ? countries.filter(country =>
      country.name.common.toLowerCase()
        .includes(query.trim().toLowerCase()))
    : []

  const output = filteredCountries.length === 1
    ? filteredCountries.map((country, countryIndex) => <div key={`country_${countryIndex}`}>
      <h1>{country.name.common}</h1>
      <p>capital : {country.capital}</p>
      <p>area: {country.area}</p>
      <h3>languages: {Object.entries(country.languages).map((language, languageIndex) => <li key={`language_${languageIndex}`}>{language}</li>
      )}</h3>
      <img src={country.flag.png} alt={country.name.common} />
    </div>)
    : filteredCountries.length < 10
      ?
      filteredCountries.map((country, countryIndex) => <div key={`country_${countryIndex}`}> <p>{country.name.common} <button onClick={() => setShow({ ...show, view: !show.view, country })}>show</button></p></div>)
      : <p>
        Too many matches, try another filter'
      </p>



  console.log(show)


  return (
    <div>
      find countries <input
        value={query}
        onChange={e => setQuery(e.target.value)} />
      {show.view ? <div>
        <h1>{show.country.name.common}</h1>
        <p>capital : {show.country.capital}</p>
        <p>area: {show.country.area}</p>
        <h3>languages: {Object.entries(show.country.languages).map((language, languageIndex) => <li key={`language_${languageIndex}`}>{language[1]}</li>
        )}</h3>
        <img src={show.country.flag.png} alt={show.country.name.common} />
      </div> : output}
    </div>

  )
}
export default App
