import React, { useState, useEffect } from 'react';
import axios from 'axios'

const App = () => {
  const [countries, setCountries] = useState([])
  const [query, setQuery] = useState('')

  useEffect(() => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data)
      })
  }, [])
  console.log(countries)

  const filteredCountries = query.trim()
    ? countries.filter(country =>
      country.name.common.toLowerCase()
        .includes(query.trim().toLowerCase()))
    : []
  return (
    <div>
      find countries <input
        value={query}
        onChange={e => setQuery(e.target.value)} />
      {filteredCountries.length < 10 ? filteredCountries.map((country, countryIndex) =>
        <li key={`country_${countryIndex}`}>{country.name.common}</li>) : filteredCountries.length === 1 ? filteredCountries.map((country, countryIndex) =>
          <li key={`country_${countryIndex}`}>
            {country.name.common},
            {country.languages},
            {country.flags},
            {country.currencies}
          </li>) : (<p>Too many matches, specify another filter</p>)}

    </div>

  )
}
export default App
