  //   : countries
  return (
    <div>
      {/* find countries <input
        value={query}
        onChange={e => setQuery(e.target.value)} /> */}
      <ul>
        {countries.name.map((name, countryIndex) =>
          <li key={`name_${countryIndex}`}>{name.common}</li>)}
      </ul>
    </div>

  )

}
export default App
