import React, { useState, useEffect } from 'react';
import axios from 'axios'

const App = () => {
  const [countries, setCountries] = useState([])
  const [query, setQuery] = useState('')

  useEffect(() => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data)
      })
  }, [])
  console.log(countries)

  const filteredCountries = query.trim()
    ? countries.filter(country =>
      country.name.common.toLowerCase()
        .includes(query.trim().toLowerCase()))
    : []

  const addDetails =
    filteredCountries.map((country, countryIndex) => <div key={`country_${countryIndex}`}>
      <h1>{country.name.common}</h1>
      <p>capital : {country.capital}</p>
      <p>area: {country.area}</p>
      <h3>languages: {Object.entries(country.languages).map((language, languageIndex) => <li key={`language_${languageIndex}`}>{language}</li>
      )}</h3>
      <img src={country.flag.png} alt={country.name.common} />
    </div>)


  return (
    <div>
      find countries <input
        value={query}
        onChange={e => setQuery(e.target.value)} />
      {/* {filteredCountries.length === 1 ? filteredCountries.map((country, countryIndex) =>
        <div key={`country_${countryIndex}`}>
          <h1>{country.name.common}</h1>
          <p>capital: {country.capital} </p>
          <p>area: {country.area} </p>
          <h2>languages: {Object.entries(country.languages).map((language, languageIndex) => <p key={`language_${languageIndex}`}>{language}</p>)}</h2>
          <ul>
          </ul>
          <img src={country.flags.png} alt={country.name.common} />
        </div>) : filteredCountries.length < 10 ? filteredCountries.map((country, countryIndex) =>
          <li key={`country_${countryIndex}`}>{country.name.common}</li>) : (<p>Too many matches, specify another filter</p>)} */}
      {filteredCountries.length === 1
        ? filteredCountries.map((country, countryIndex) => <div key={`country_${countryIndex}`}>
          <h1>{country.name.common}</h1>
          <p>capital : {country.capital}</p>
          <p>area: {country.area}</p>
          <h3>languages: {Object.entries(country.languages).map((language, languageIndex) => <li key={`language_${languageIndex}`}>{language}</li>
          )}</h3>
          <img src={country.flag.png} alt={country.name.common} />
        </div>)
        : filteredCountries.length < 10
          ? <button onClick={addDetails}>show</button>
          // filteredCountries.map((country, countryIndex) => <li key={`country_${countryIndex}`}>{country.name.common}</li>)
          : 'Too many matches, try another filter'
      }
    </div>

  )
}
export default App
