import React, { useState, useEffect } from 'react'
import axios from 'axios'

const App = () => {
  const [countries, setCountries] = useState([])
  const [query, setQuery] = useState('')


  useEffect(() => {
    axios.get('https://restcountries.com/v3.1/all')
      .then(response => {
        // console.log(response);
        setCountries(response.data)
      })
  }, [])
  console.log(countries);

  const filteredCountries = query.trim()
    ? countries.filter(country => country.name.common.toLowerCase()
      .includes(query.trim().toLowerCase()))
    : []
  return (
    <div>
      find countries <input value={query}
        onChange={e => setQuery(e.target.value)} />

      {filteredCountries.length === 1
        ? filteredCountries.map((country, countryIndex) =>
          <div key={`country_${countryIndex}`}>
            <h1>{country.name.common}</h1>
            <p>capital {country.capital}</p>
            <p>area {country.area}</p>
            <h3>
              languages: {Object.entries(country.languages).map((languages, languageIndex) =>
                <li key={`language_${languageIndex}`}>{languages[1]}</li>)}
            </h3>
          </div>)

        : filteredCountries.length < 10
          ? filteredCountries.map((country, countryIndex) =>
            <li key={`country_${countryIndex}`}>{country.name.common}</li>)
          : <p>Too many matches, specify another filter</p>}
    </div>
  )
}

export default App
























